vector<int> compose(const vector<int> &a, const vector<int> &b){
	int len = a.size();
	vector<int> r(len);
	for(int i = 0; i < len; i++)
		r[i] = a[b[i]];
	return r;
}
vector<int> inverse(const vector<int> &a){
	int len = a.size();
	vector<int> r(len);
	for(int i = 0; i < len; i++)
		r[a[i]] = i;
	return r;
}
void show(const vector<int> &a){
	int len = a.size();
	for(int i = 0; i < len; i++)
		cout << a[i] << " ";
	cout << endl;
}
vector<int> pwr(vector<int> a, int k){
	int len = a.size();
	vector<int> r(len);
	for(int i = 0; i < len; i++)
		r[i] = i;
	for(; k; k >>= 1, a = compose(a, a))
		if(k&1)
			r = compose(r, a);
	return r;
}
/**
0 = p
1 = q
2 = qp-1
3 = qp-1q-1
4 = qp-1q-1pq-1
5 = qp-1q-1pq-1qpq-1 = qp-1q-1ppq-1
6 = qp-1q-1ppq-1qp-1qpq-1 = qp-1q-1p qpq-1

0 = (I, p)
1 = (I, q)
2 = (I, qp-1)
3 = (q, p-1)
4 = (qp-1, q-1)
5 = (qp-1, q-1p)
**/
int main(){
	int n, k; cin >> n >> k;
	vector<int> p(n), q(n);
	for(int i = 0; i < n; i++) cin >> p[i], p[i]--;
	for(int i = 0; i < n; i++) cin >> q[i], q[i]--;
	vector<int> fold = compose(q, compose(inverse(p), compose(inverse(q), p)));
	k--;
	fold = pwr(fold, k / 6); k %= 6;
	if(k == 0){
		fold = compose(fold, compose(p, inverse(fold)));
	} else if(k == 1){
		fold = compose(fold, compose(q, inverse(fold)));
	} else if(k == 2){
		fold = compose(fold, compose(compose(q, inverse(p)), inverse(fold)));
	} else if(k == 3){
		fold = compose(compose(fold, q), compose(inverse(p), inverse(compose(fold, q))));
	} else if(k == 4){
		vector<int> a = compose(q, inverse(p));
		fold = compose(fold, a);
		fold = compose(fold, compose(inverse(q), inverse(fold)));
	} else {
		vector<int> a = compose(q, inverse(p));
		fold = compose(fold, a);
		fold = compose(fold, compose(compose(inverse(q), p), inverse(fold)));
	}
	for(int i = 0; i < n; i++) cout << fold[i] + 1 << " ";
	cout << endl;
	return 0;
}

