int N,a,b;

vector<int> ask(int len, int fst, int lst, int off){

  vi ans;

  if (len == 1){
    ans.pb(fst+off);
    ans.pb(lst+off);
    return ans;
  }
  int H = (1<<(len-1));
  int fb=(H&fst)>0;
  int lb=(H&lst)>0;

  // cout<<fst<<' '<<lst<<'\n';

  if (fb == lb && fb==1){
    vi A = ask(len-1, fst-H,lst-H,H);
    int j=A.back();A.pop_back();
    int k=A.back();
    vi B=ask(len-1,k-H,j-H,0);
    for (auto i:A)ans.pb(i);
    for (auto i:B)ans.pb(i);
    ans.pb(j);
  }
  else if (fb == lb && fb == 0){
    vi A = ask(len-1,fst,lst,0);
    int j=A.back();A.pop_back();
    int k=A.back();
    vi B=ask(len-1,k,j,H);
    for (auto i:A)ans.pb(i);
    for (auto i:B)ans.pb(i);
    ans.pb(j);
    // for (auto i :ans)cout<<i<<' ';
  }else{
    int nst = fst%H;
    int nen = lst%H;
    int tar=0;
    while (tar==nst||tar==nen)++tar;
    if (fst>H){
      vi A = ask(len-1,nst,tar,H);
      vi B = ask(len-1,tar,nst,0);
      for (auto i:A)ans.pb(i);
      for (auto i:B)ans.pb(i);
    }else{
      vi A = ask(len-1,nst,tar,0);
      vi B = ask(len-1,tar,nst,H);
      for (auto i:A)ans.pb(i);
      for (auto i:B)ans.pb(i);
    }
  }
  for (auto &i:ans)i+=off;
  // for (auto i:ans)cout<<i<<' ';cout<<'\n';
  return ans;
}

