vector<int>mul(vector<int>a,vector<int>b){
	int n=a.size();
	vector<int>ret(n);
	for(int i=0;i<n;i++){
		ret[i]=a[b[i]];
	}
	return ret;
}
vector<int>pw(vector<int>a,int b){
	int n=a.size();
	vector<int>ret(n);
	for(int i=0;i<n;i++)ret[i]=i;
	while(b){
		if(b%2){
			vector<int>tmp(n);
			for(int i=0;i<n;i++){
				tmp[i]=ret[a[i]];
			}
			ret=tmp;
		}
		b/=2;
		vector<int>tmp(n);
		for(int i=0;i<n;i++){
			tmp[i]=a[a[i]];
		}
		a=tmp;
	}
	return ret;
}
