		L[i]=R[i]=i;
	}
	vector<int>cycle=mul(mul(pi,q),mul(p,qi));
	vector<int>Lcycle=mul(mul(q,pi),mul(qi,p));

	R=pw(cycle,Rlen/4);
	if(Rlen%4>0)R=mul(qi,R);
