using namespace std;

#define all(x) (x).begin(), (x).end()

typedef vector<int> vi;
typedef vector<vi> vvi;
typedef long long ll;

vi rev(vi p)
{
    vi q(p.size());
    for (int i = 0; i < p.size(); i++) q[p[i]] = i;
    return q;
}

vi apply(vi p, vi q)
{
    vi r(p.size());
    for (int i = 0; i < p.size(); i++) r[i] = p[q[i]];
    return r;
}

vi inq(vi A, int k)
{
    if (k == 0)
    {
        vi a(A.size());
        iota(all(a), 0);
        return a;
    }
    vi B = inq(A, k / 2);
    if (k % 2) return apply(B, apply(B, A));
    return apply(B, B);
}

void solve(int n, int k, vi p, vi q)
{
    k--;
    vi A = apply(q, apply(rev(p), apply(rev(q), p)));
    A = inq(A, k / 6);
    vector<vi> a(6);
    a[0] = apply(A, apply(p, rev(A)));
    a[1] = apply(A, apply(q, rev(A)));
    for (int i = 2; i < 6; i++)
    {
        a[i] = apply(a[i - 1], rev(a[i - 2]));
    }
    for (int i = 0; i < n; i++)
    {
        cout << a[k % 6][i] + 1 << " ";
    }
    cout << "\n";
}

int main()
{
    int n, k;
    cin >> n >> k;
    vi p(n), q(n);
    for (int i = 0; i < n; i++) cin >> p[i], p[i]--;
    for (int i = 0; i < n; i++) cin >> q[i], q[i]--;
    solve(n, k, p, q);
}

