vector<int> compose(const vector<int> &v1,const vector<int> &v2){
  vector<int> v;
  REP(i,v1.size())v.push_back(v1[v2[i]]);
  return v;
}

vector<int> inverse(const vector<int> &v){
  vector<int> retv(v.size());
  REP(i,v.size())retv[v[i]]=i;
  return retv;
}

