const int Maxn=100005;
int n,k;
struct P{
	int a[Maxn];
	P(){
		memset(a,0,sizeof(a));
	}
	void Out(){
		for (int i=1;i<=n;i++){
			printf("%d ",a[i]);
		}
	}
}p,q;
P inv(P x){
	P ret;
	for (int i=1;i<=n;i++){
		ret.a[x.a[i]]=i;
	}
	return ret;
}
P mul(P x,P y){
	P ret;
	for (int i=1;i<=n;i++){
		ret.a[i]=y.a[x.a[i]];
	}
	return ret;
}
P qp(P x,int pw){
	P ret;
	for (int i=1;i<=n;i++){
		ret.a[i]=i;
	}
	while (pw){
		if (pw&1) ret=mul(ret,x);
		x=mul(x,x);
		pw>>=1;
	}
	return ret;
}
int main(){
	scanf("%d %d",&n,&k);
	for (int i=1;i<=n;i++){
		scanf("%d",&p.a[i]);
	}
	for (int i=1;i<=n;i++){
		scanf("%d",&q.a[i]);
	}
	P pr=inv(p),qr=inv(q);
	if (k==1){
		p.Out();
		return 0;
	}
	if (k==2){
		q.Out();
		return 0;
	}
	if (k==3){
		P ret=mul(qr,p);
		ret.Out();
		return 0;
	}
	k-=4;
	P tmp=mul(qr,mul(p,mul(q,pr)));
	int W=k/6;
	k%=6;
	tmp=qp(tmp,W);
	if (k==0){
		tmp=mul(tmp,qr);
		P ans=mul(tmp,mul(pr,inv(tmp)));
		ans.Out();
	}
	else if (k==1){
		tmp=mul(tmp,mul(qr,p));
		P ans=mul(tmp,mul(qr,inv(tmp)));
		ans.Out();
	}
	else if (k==2){
		tmp=mul(tmp,mul(qr,p));
		P ans=mul(tmp,mul(mul(p,qr),inv(tmp)));
		ans.Out();
	}
	else if (k==3){
		tmp=mul(tmp,mul(qr,mul(p,q)));
		P ans=mul(tmp,mul(p,inv(tmp)));
		ans.Out();
	}
	else if (k==4){
		tmp=mul(tmp,mul(qr,mul(p,mul(q,pr))));
		P ans=mul(tmp,mul(q,inv(tmp)));
		ans.Out();
	}
	else if (k==5){
		tmp=mul(tmp,mul(qr,mul(p,mul(q,pr))));
		P ans=mul(tmp,mul(mul(pr,q),inv(tmp)));
		ans.Out();
	}

	return 0;
}

