const int SIZE = 1e6+10;
int p[SIZE],q[SIZE],invp[SIZE],invq[SIZE],d[SIZE],e[SIZE],inve[SIZE];
int a[6][SIZE];
bool u[SIZE];
int main(){
    int N,K; R(N,K);
    FOR(i,1,N)R(p[i]);
    FOR(i,1,N)R(q[i]);
    FOR(i,1,N){
        invp[p[i]]=i;
        invq[q[i]]=i;
    }
    memcpy(a[0],p,sizeof(p));
    memcpy(a[1],q,sizeof(q));
    FOR(i,2,5){
        FOR(j,1,N){
            a[i][a[i-2][j]]=a[i-1][j];
        }
    }
    FOR(i,1,N){
        d[i]=invp[q[p[invq[i]]]];
    }
    K--;
    int tt=K/6;
    FOR(i,1,N){
        if(!u[i]){
            VI gg;
            int now=i;
            while(!u[now]){
                gg.PB(now);
                u[now]=1;
                now=d[now];
            }
            REP(j,SZ(gg)) e[gg[j]]=gg[(j+tt)%SZ(gg)];
        }
    }
    FOR(i,1,N)inve[e[i]]=i;
    VI an;
    FOR(i,1,N)an.PB(inve[a[K%6][e[i]]]);
    W(an);
    return 0;
}
