int n;
vector < int > poly_mult(vector < int > a, vector < int > b) {
    vector < int > c(n);
    for (int i = 0; i < n; i++) {
        c[i] = a[b[i]];
    }
    return c;
}
vector < int > inv(vector < int > a) {
    vector < int > c(n);
    for (int i = 0; i < n; i++) {
        c[a[i]] = i;
    }
    return c;
}
vector < int > pw(vector < int > a, int k) {
    vector < int > ans(n);
    if (k == 0) {
        for (int i = 0; i < n; i++) ans[i] = i;
        return ans;
    }
    if (k == 1) return a;
    if (k & 1) return poly_mult(a, pw(a, k - 1));
    auto t = pw(a, k / 2);
    return poly_mult(t, t);
}
int k;
void print(vector < int > p) {
    for (int v : p) cout << v + 1 << " ";
    exit(0);
}
