    for (int i = 0; i < n; i++) {
        cin >> q[i];
        q[i]--;
    }
    while (k % 6 != 1) {
        auto t1 = poly_mult(q, inv(p));
        auto t2 = q;
        q = t1;
        p = t2;
        k--;
    }
    if (k == 1) {
        print(p);
    }
    auto pp = p;
    auto qq = q;
    for (int i = 0; i < 2; i++) {
        auto t = poly_mult(qq, inv(pp));
        pp = qq;
        qq = t;
    }
    // a[4] = qq;
    auto T = poly_mult(qq, p);
    auto T1 = pw(T, k / 6);
