vector<int> merge(vector<int> a, const vector<int>& b) {
	for (int x : b) {
		if (!a.empty() && a.back() == (x ^ 1)) {
			a.pop_back();
		} else {
			a.push_back(x);
		}
	}
	return a;
}

void inv(vector<int>& a) {
	reverse(all(a));
	for (int& x : a) {
		x ^= 1;
	}
}

