vector<int> mult(const vector<itn>& a, const vector<itn>& b) {
	int n = a.size();
	vector<int> c(n);
	for (int i = 0; i < n; ++i) {
		c[i] = a[b[i]];
	}
	return c;
}

vector<int> pw(vector<int> a, int b) {
	vector<int> res(a.size());
	iota(all(res), 0);
	while (b) {
		if (b & 1) {
			res = mult(res, a);
		}
		b >>= 1;
		a = mult(a, a);
	}
	return res;
}

int main() {
