	int n = nxt(), k = nxt();
	vector<int> p(n), q(n);
	for (int i = 0; i < n; ++i) {
		p[i] = nxt() - 1;
	}
	for (int i = 0; i < n; ++i) {
		q[i] = nxt() - 1;
	}
	vector<itn> P(n), Q(n);
	for (int i = 0; i < n; ++i) {
		P[p[i]] = Q[q[i]] = i;
	}

	auto qPQp = mult(mult(q, P), mult(Q, p));
	auto PqpQ = mult(mult(P, q), mult(p, Q));

	if (k == 1) {
		for (int x : p) {
			printf("%d ", x + 1);
		}
		printf("\n");
		return 0;
	}

	int len = (k - 2) / 3 * 4 + 1 + (k - 2) % 3;
	int lhs = (len + 2) / 2;
	int rhs = len - lhs;

	auto res = pw(PqpQ, rhs / 4);
	if (rhs % 4 >= 1) {
		res = mult(Q, res);
	}
	if (rhs % 4 >= 2) {
		res = mult(p, res);
	}
	if (rhs % 4 >= 3) {
		res = mult(q, res);
	}
	if (lhs % 4 >= 3) {
		res = mult(Q, res);
	}
	if (lhs % 4 >= 2) {
		res = mult(P, res);
	}
	if (lhs % 4 >= 1) {
		res = mult(q, res);
	}
	res = mult(pw(qPQp, lhs / 4), res);

	for (int x : res) {
		printf("%d ", x + 1);
	}
	printf("\n");

	return 0;
}

#include <bits/stdc++.h>
using namespace std;

int n, k;
typedef vector<int> perm;
perm operator + (const perm &a, const perm &b) {
  perm c(n, 0);
  for (int i = 0; i < n; i++) {
    c[i] = b[a[i]];
  }
  return c;
}
perm operator ~ (const perm &a) {
  perm c(n);
  for (int i = 0; i < n; i++) {
    c[a[i]] = i;
  }
  return c;
}

int main() {
  scanf("%d%d", &n, &k);
  perm a(n), b(n);
  for (int i = 0; i < n; i++) {
    scanf("%d", &a[i]);
    --a[i];
  }
  for (int i = 0; i < n; i++) {
    scanf("%d", &b[i]);
    --b[i];
  }
  perm p = ~b + a + b + ~a, q(n);
  vector<int> visit(n, 0), d;
  for (int i = 0; i < n; i++) {
    if (!visit[i]) {
      d.clear();
      int j = i;
      do {
        d.push_back(j);
        visit[j] = 1;
        j = p[j];
      } while (j ^ i);
      for (int j = 0; j < (int) d.size(); j++) {
        q[d[j]] = d[(j + (k - 1) / 6) % d.size()];
      }
    }
  }
  a = q + a + ~q;
  b = q + b + ~q;
  for (int t = (k - 1) % 6; t; t--) {
    perm c = ~a + b;
    a = b, b = c;
  }
  for (int i = 0; i < n; i++) {
    printf("%d%c", a[i] + 1, i == n - 1 ? '\n' : ' ');
  }
}

