const int N = 1e5+5;

int n,k,p[N],q[N],ip[N],iq[N],tr[N];
int tmp[N];

void multi(int *a,int *b,int *c){
	fo(i,1,n)tmp[i]=a[b[i]];
	fo(i,1,n)c[i]=tmp[i];
}

void inverse(int *a,int *b){
	fo(i,1,n)tmp[a[i]]=i;
	fo(i,1,n)b[i]=tmp[i];
}

bool pd[N];
int cir[N],u;
int ans[N];

int main(){
	n=get();k=get();
	fo(i,1,n)p[i]=get();
	fo(i,1,n)q[i]=get();
	if (k==1){
		fo(i,1,n)printf("%d ",p[i]);putchar('\n');
		return 0;
	}
	if (k==2){
		fo(i,1,n)printf("%d ",q[i]);putchar('\n');
		return 0;
	}
	fo(i,1,n)ip[p[i]]=i,iq[q[i]]=i;
	fo(i,1,n)tr[i]=i;
	multi(q,ip,tr);
	multi(tr,iq,tr);
	multi(tr,p,tr);
	int tim=(k-1)/6,pos=(k-1)%6+1;
	fo(i,1,n)
	if (!pd[i]){
		u=0;
		for(int x=i;!pd[x];x=tr[x])pd[cir[++u]=x]=1;
		fo(j,1,u)ans[cir[j]]=cir[(j+tim-1)%u+1];
	}
	fo(i,1,n)tr[i]=ans[i];
	if (pos==1)fo(i,1,n)ans[i]=p[i];
	if (pos==2)fo(i,1,n)ans[i]=q[i];
	if (pos==3)multi(q,ip,ans);
	if (pos==4){multi(tr,q,tr);fo(i,1,n)ans[i]=ip[i];}
	if (pos==5){multi(tr,q,tr);multi(tr,ip,tr);fo(i,1,n)ans[i]=iq[i];}
	if (pos==6){multi(iq,p,ans);multi(tr,q,tr);multi(tr,ip,tr);}
	multi(tr,ans,ans);
	inverse(tr,tr);
	multi(ans,tr,ans);
	fo(i,1,n)printf("%d ",ans[i]);putchar('\n');
	return 0;
}

