vector<int> inv(vector<int>&a){
    vector<int>b(a.size());
    rep(i,a.size()){
        b[a[i]] = i;
    }
    return b;
}

vector<int> mul(vector<int>&a,vector<int>&b){
    int n = a.size();
    vector<int> res(n);
    vector<int> ib = inv(b);
    rep(i,n){
        res[ib[i]] = a[i];
    }
    return res;
}
vector<int> calc(vector<int>a,int k){

    if(k==0){
        int n = a.size();
        vector<int> res(n);
        rep(i,n){
            res[i] = i;
        }
        return res;
    }
    if(k==1){
        return a;
    }
    if(k%2==0){
        vector<int> tmp = calc(a,k/2);
        return mul(tmp,tmp);
    }else{
        vector<int> tmp = calc(a,k/2);
        tmp = mul(tmp,tmp);
        return mul(a,tmp);
    }
}
