    int d = k%6;
    vector<int> a,b;
    if(d==0){
        b = p;
    }else if(d==1){
        b = q;
    }else if(d==2){
        vector<int> tmp = inv(p);
        b = mul(q,tmp);
    }else if(d==3){ 
        b = inv(p);
    }else if(d==4){
        b = inv(q);
    }else if(d==5){
        vector<int> tmp = inv(q);
        b = mul(tmp,p);
    }
    vector<int> ip,iq;
    ip = inv(p);
    iq = inv(q);
    a = mul(q,ip);
    a = mul(a,iq);
    a = mul(a,p);
    a = calc(a,c);
    if(d >= 3){
        a = mul(a,q);
        a = mul(a,ip);
    }
    vector<int> ia = inv(a);
