vector<int> perm_pow(vector<int> base, ll k) {
	int n = base.size();
	vector<int> ans(n);
	rep(i, n) ans[i] = i;
	while (k) {
		if (k & 1) ans *= base;
		base *= base;
		k >>= 1;
	}
	return ans;
}

signed main() {
