	rep(i, n) {
		cin >> q[i];
		q[i]--;
		qinv[q[i]] = i;
	}
	vector<int> ans = q;
	if (k == 1) ans = p;
	if (k == 3) ans *= pinv;
	else {
		vector<int> x = pinv * qinv * p * q;
		ans *= perm_pow(x, (k - 1) / 6);
		if ((k - 1) % 6 >= 3) ans *= pinv * qinv;
		if (k % 6 == 0) ans *= p;
		if (k % 6 == 3) ans *= pinv;
		if ((k - 2) % 6 >= 3) ans *= p * qinv;
		vector<int> y = pinv * q * p * qinv;
		ans *= perm_pow(y, (k - 2) / 6);
	}
	rep(i, n) cout << ans[i] + 1 << ' ';
	cout << '\n';
}

