    Permutation p(a),q(b);
    Permutation l=q.inv()*p*q*p.inv();
    l=l.pow((k-2)/6);
    if((k-2)%6>=2)l*=q.inv();
    if((k-2)%6>=3)l*=p;
    if((k-2)%6>=5)l*=q;
    Permutation r=p*q.inv()*p.inv()*q;
    r=r.pow(k/6);
    if(k%6>=2)r=q*r;
    if(k%6>=3)r=p.inv()*r;
    if(k%6>=5)r=q.inv()*r;
    l*=r;
    each(i,l.perm)i++;
    out(l.perm);
}

