vector<LL> func(vector<LL> a, vector<LL> b) {
  vector<LL> ret(a.size());
  for (int i = 0; i < a.size(); i++) ret[a[i]] = b[i];
  return ret;
}

vector<LL> inv(vector<LL> a) {
  vector<LL> ret(a.size());
  for (int i = 0; i < a.size(); i++) ret[a[i]] = i;
  return ret;
}

vector<LL> mult(vector<LL> a, vector<LL> b) {
  vector<LL> ret(a.size());
  for (int i = 0; i < a.size(); i++) ret[i] = a[b[i]];
  return ret;
}

vector<LL> pow(vector<LL> a, int k) {
  if (k == 0) {
    vector<LL> ret(a.size());
    for (int i = 0; i < a.size(); i++) ret[i] = i;
    return ret;
  }
  if (k == 1) return a;
  vector<LL> ret = pow(a, k / 2);
  ret = mult(ret, ret);
  if (k % 2 == 1) {
    ret = mult(ret, a);
  }
  return ret;
}

void check(vector<LL> p, vector<LL> q, int K) {
  K--;
  vector<vector<LL>> data;
  data.push_back(p);
  data.push_back(q);
  for (int i = 2; i <= 20; i++) {
    data.push_back(func(data[i - 2], data[i - 1]));
  }

  auto tmp = mult(data[9], data[6]);
  auto tmpinv = inv(tmp);
  auto base = data[K % 6];
  base = mult(base, pow(tmpinv, K / 6));
  base = mult(pow(tmp, K / 6), base);
  for (int i = 0; i < base.size(); i++) {
    cout << base[i] + 1;
    if (i < base.size() - 1)
      cout << " ";
    else
      cout << endl;
  }
}

void solve(long long N, long long K, std::vector<long long> p,
           std::vector<long long> q) {
  for (auto&& i : p) i--;
  for (auto&& i : q) i--;

  check(p, q, K);
}

int main() {
  cout << setprecision(10);
  long long N;
  scanf("%lld", &N);
  long long K;
  scanf("%lld", &K);
  std::vector<long long> p(N);
  for (int i = 0; i < N; i++) {
    scanf("%lld", &p[i]);
  }
  std::vector<long long> q(N);
  for (int i = 0; i < N; i++) {
    scanf("%lld", &q[i]);
  }
  solve(N, K, std::move(p), std::move(q));
  return 0;
}

