vector<int> inverse(vector<int> p) {
	int n = p.size() - 1;
	vector<int> ans(n + 1);
	for (int i = 1; i <= n; ++i) {
		ans[p[i]] = i;
	}
	return ans;
}

vector<int> compose(vector<int> p, vector<int> q) {
	int n = p.size() - 1;
	vector<int> ans(n + 1);
	for (int i = 1; i <= n; ++i) {
		ans[i] = p[q[i]];
	}
	return ans;
}

vector<int> power(vector<int> p, int x) {
	memset(seen, 0, sizeof(seen));
	int n = p.size() - 1;
	vector<int> ans(n + 1);
	for (int i = 1; i <= n; ++i) {
		if (seen[i]) {
			continue;
		}
		vector<int> cycle;
		int u = p[i];
		while (!seen[u]) {
			seen[u] = true;
			cycle.pb(u);
			u = p[u];
		}
		int k = cycle.size();
		for (int j = 0; j < k; ++j) {
			ans[cycle[j]] = cycle[(j + x) % k];
		}
	}
	return ans;
}

