using namespace std;

const int MAX_N = 1e5 + 10;

int N, K;

struct Perm {
    int p[MAX_N];
    int &operator[](int x) { return p[x]; }
    const int &operator[](int x) const { return p[x]; }

    Perm() { iota(p + 1, p + N + 1, 1); }

    Perm kth_power(int k) {
        Perm res;
        static bool vis[MAX_N];
        fill_n(vis + 1, N, false);
        for (int i = 1; i <= N; i++) {
            if (!vis[i]) {
                vector<int> cycle;
                for (int j = i; !vis[j]; vis[j] = true, j = p[j]) cycle.push_back(j);
                int n = cycle.size();
                for (int j = 0; j < n; j++)
                    res[cycle[j]] = cycle[(j + n + k) % n];
            }
        }
        return res;
    }
};

Perm mult(const Perm &A, const Perm &B) {
    Perm C;
    for (int i = 1; i <= N; i++) C[i] = B[A[i]];
    return C;
}

