    small_cases[1] = P;
    small_cases[2] = Q;
    for (int i = 3; i <= 6; i++) {
        small_cases[i] = mult(small_cases[i - 2].kth_power(-1), small_cases[i - 1]);
    }
    int m = (K - 1) / 6;
    Perm res = small_cases[K - 6 * m], head = Q.kth_power(-1);
    head = mult(head, P);
    head = mult(head, Q);
    head = mult(head, P.kth_power(-1));
    head = head.kth_power(m);
    res = mult(head, res);
    res = mult(res, head.kth_power(-1));
