struct permu{
	vector < int > p;
	void read(){
		p.resize(n);
		int i;
		for(i=0;i<n;++i){
			scanf("%d",&p[i]);
			--p[i];
		}
	}
	void print(){
		int i;
		for(i=0;i<n;++i){
			printf("%d ",p[i]+1);
		}
		printf("\n");
	}
	void init(){
		p.resize(n);
		int i;
		for(i=0;i<n;++i) p[i]=i;
	}
};
permu M(permu A,permu B){
	permu ret;
	ret.init();
	int i;
//	printf("%d %d\n",(int)A.p.size(),(int)B.p.size());
	for(i=0;i<n;++i){
		ret.p[i]=B.p[A.p[i]];
	}
	return ret;
}
permu P(permu A,int T){
	permu R;
	R.init();
	while(T){
		if(T&1) R=M(R,A);
		T>>=1;
		A=M(A,A);
	}
	return R;
}
permu inv(permu A){
	permu ret;
	ret.init();
	int i;
	for(i=0;i<n;++i) ret.p[A.p[i]]=i;
	return ret;
}
permu p,q,ip,iq;
permu eval(int v){
	if(v==1)
		return p;
	else if(v==-1)
		return ip;
	else if(v==2)
		return q;
	else
		return iq;
}
int getsz(int m){
	if(m==1) return 1;
	int id=(m+1)/3;
	return (id-1)*4+1+(m+1)%3;
}
