	permu LLL=P(ALLP,((S&1)?(S/2):(S/2-1))/4),MMM=eval(fmd[(m-1)%6]),RRR;
	int t=((S&1)?(S/2):(S/2-1))%4;
	for(i=0;i<t;++i) LLL=M(LLL,eval(fhp[i]));
	RRR=inv(LLL);
