typedef vector<int> vi;

int norm(int x, int m) {
  x %= m;
  if (x < 0) x += m;
  return x;
}

vi power(const vi& p, int k) {
  int n = p.size();
  vector<bool> vis(n);
  vi ret(n);
  for (int i = 0; i < n; ++i)
    if (!vis[i]) {
      vector<int> cycle;
      int x = i;
      while (!vis[x]) {
        cycle.push_back(x);
        vis[x] = true;
        x = p[x];
      }
      for (int i = 0; i < cycle.size(); ++i)
        ret[cycle[i]] = cycle[norm(i + k, cycle.size())];
    }
  return ret;
}

vi inverse(const vi& p) { return power(p, -1); }

vi mult(const vi& p, const vi& q) {
  int n = p.size();
  vi ret(n);
  for (int i = 0; i < n; ++i)
    ret[i] = p[q[i]];
  return ret;
}

template <class T, class... Args>
vi mult(const T& first, const Args&... args...) {
  return mult(first, mult(args...));
}

