    if (k == 1) {
        for (int i = 0; i < n - 1; i++) cout << p[i] << " ";
        cout << p[n - 1] << endl;
    } 
    else if (k == 2) {
        for (int i = 0; i < n - 1; i++) cout << q[i] << " ";
        cout << q[n - 1] << endl;
    } 
    else {
        for (int i = 2; i < k; i++)
        {
            if (i == 3)
                for (int i = 0; i < n; i++) loop_p[i] = p[i];
            if (i == 4)
                for (int i = 0; i < n; i++) loop_q[i] = q[i];
            cnt++;
            for (int j = 0; j < n; j++)
                tmp[(p[j] - 1)] = q[j];
            for (int j = 0; j < n; j++) {
                p[j] = q[j];
                q[j] = tmp[j];
            }

            if (is_equal(loop_p, p, n) && is_equal(loop_q, q, n))
            {
                for (int i = 0; i < ((k - 2) % cnt); i++) {
                    for (int j = 0; j < n; j++)
                        tmp[(p[j] - 1)] = q[j];
                    for (int j = 0; j < n; j++) {
                        p[j] = q[j];
                        q[j] = tmp[j];
                    }
                }
                break;
            }
        }
        for (int i = 0; i < n - 1; i++)
            cout << q[i] << " ";
        cout << q[n - 1] << endl;
    }
}

bool is_equal(int loop[], int q[], int n)
{
    for (int i = 0; i < n; i++) {
        if (loop[i] != q[i]) return false;
    }
    return true;
}

