using X = vector<int>;

X inverse(X x) {
  X r(x.size());
  for(int i = 0; i < x.size(); i++) r[x[i]] = i;
  return r;
}

X op(X a, X b) {
  X r(a.size());
  for(int i = 0; i < a.size(); i++) r[i] = b[a[i]];
  return r;
}

X f(X a, X b) {
  X r(a.size());
  for(int i = 0; i < a.size(); i++) r[a[i]] = b[i];
  return r;
}

int n, k;

X getE() {
  X r(n);
  iota(begin(r), end(r), 0);
  return r;
}

X x[4];
int cycle[] = {2, 0, 1, 3};

// 

X get(int start, int t) {
  int t4 = t / 4;
  auto a = getE();
  for(int i = 0; i < 4; i++) a = op(a, x[cycle[(start + i) % 4]]);
  auto r = getE();
  while(t4) {
    if(t4 & 1) r = op(r, a);
    a = op(a, a);
    t4 >>= 1;
  }
  t %= 4;
  for(int i = 0; i < t; i++) r = op(r, x[cycle[(start + i) % 4]]);
  return r;
}


