using namespace std;
typedef long long ll;


struct permutation {
    int sz;
    vector<int> a;
    permutation(int N = 0): sz(N), a(N + 10) { }
    inline void identity() {
        for(int i = 1; i <= sz; ++i)  a[i] = i;
    }
    inline void show(const char * msg) {
        printf("%s", msg);
        for(int i = 1; i <= sz; ++i) {
            printf("%d", a[i]);
            if(i < sz) putchar(' ');
        }
    }
    int& operator [] (int idx) { return a[idx]; }
    permutation operator * (permutation rhs) {
        permutation res(sz);
        for(int i = 1; i <= sz; ++i)
            res[i] = a[rhs[i]];
        return res;
    }
    bool check() {
        vector<int> _vis(sz + 3, 0);
        rep(i, sz) _vis[a[i]] = true;
        rep(i, sz) if(!_vis[i]) { puts("NO!NOT A PERMUTATION!"); return false; }
        return true;
    }
    ll decomp() {
        vector<int> _vis(sz + 3, 0);
        ll ans = -1;
        for(int i = 1; i <= sz; ++i) if(!_vis[i]) {
            ll len = 0, c = i;
            while(!_vis[c]) {
                _vis[c] = true;
                len++;
                c = a[c];
            }
            if(ans == -1) ans = len;
            else ans = ans * (len / __gcd(ans, len));
        }
        return ans;
    }
    permutation inverse() {
        permutation rev(sz);
        rep(i, sz) rev[a[i]] = i;
        return rev;
    }
};

permutation qpow(permutation p, int y) {
    permutation t(p.sz);
    t.identity();
    for(; y; y >>= 1, p = p * p)
        if(y & 1) t = t * p;
    return t;
}

int n, k;
int main() {
    scanf("%d%d", &n, &k);
    int period = (k - 1) / 6, left = k - 6 * period;
    permutation p(n), q(n), pi(n), qi(n), res(n), A(n), B(n), qpqp(n);
    rep(i, n) scanf("%d", &p[i]);
    rep(i, n) scanf("%d", &q[i]);
    pi = p.inverse();
    qi = q.inverse();
    qpqp = q * pi * qi * p;
    res.identity();

    A = qpow(qpqp, period);
    switch(left) {
        case 1: B = p; break;
        case 2: B = q; break;
        case 3: B = q * pi; break;
        case 4: B = q * pi * qi; break;
        case 5: B = q * pi * qi * p * qi; break;
        case 6: B = q * pi * qi * p * p * qi; break;
    }

    res = A * B * A.inverse();
    res.show("");
    putchar('\n');

    return 0;
}

