vector<int> power(vector<int> a, int n) {
    int sz = a.size();
    vector<int> res(sz);
    for (int i = 0; i < sz; ++i)
        res[i] = i;
    while (n) {
        if (n & 1)
            res = mult(res, a);
        a = mult(a, a);
        n /= 2;
    }
    return res;
}



