vector<int> fast_pow(const vector<int>& A, int e) {
    if (e == 0) {
        vector<int> res(N+1);
        for (int i = 1; i <= N; i++) res[i] = i;
        return res;
    }
    if (e%2 == 0) return fast_pow(mul(A, A), e/2);
    return mul(A, fast_pow(mul(A, A), e/2));
}

