				printf("%d ",v[i]);
			printf("\n");
		}
	};
	perm e(int n)
	{
		perm ans;
		ans.resize(n);
		for(int i=1;i<=n;i++)
			ans[i]=i;
		return ans;
	}
	perm operator*(perm a,perm b)
	{
		perm ans=e(a.size());
		perm rev=e(a.size());
		for(int i=1;i<=a.size();i++)
			ans[i]=a[b[i]];
		return ans;
	}
	perm operator~(perm a)
	{
		perm ans=e(a.size());
		for(int i=1;i<=a.size();i++)
			ans[a[i]]=i;
		return ans;
	}
	void find_cycle(perm a,int x,vector<int>vis,vector<int>&rem)
	{
		if(vis[x]) return;
		vis[x]=1;
		rem.push_back(x);
		find_cycle(a,a[x],vis,rem);
	}
	perm Pow(perm a,int x)
	{
		perm ans=e(a.size());
		vector<vector<int> >cycle;
		vector<int>vis;
		vis.resize(a.size()+1);
		for(int i=1;i<=a.size();i++)
		{
			if(!vis[i])
			{
				vector<int> rem;
				find_cycle(a,i,vis,rem);
				cycle.push_back(rem);
			}
		}
		for(int i=0;i<cycle.size();i++)
		{
			for(int j=0;j<cycle[i].size();j++)
				ans[cycle[i][j]]=cycle[i][(j+x)%cycle[i].size()];
		}
		return ans;
	}
