vector<int> solve(vector<int>pp, vector<int>qq, int k, int b) {
	vector<int>ppp;
	vector<int>qqq;
	vector<int>re;
	int r[N];
	int cb;
	if (k == 1)return pp;
	if (k == 2)return qq;
	if (k == 3) {
		f(i, pp.size())r[pp[i]] = qq[i];
		f(i, pp.size())re.push_back(r[i + 1]);
		return re;
	}
	if (s.count(make_pair(k, b)) == 1)return m[make_pair(k, b)];
	if (k % 2 == 1) {
		ppp = solve(pp, qq, (k + 1) / 2, b);
		qqq = solve(pp, qq, (k + 3) / 2, b);
		bb++;
		cb = b;
		b = bb;
		re = solve(ppp, qqq, (k + 1) / 2, b);
		s.insert(make_pair(k, cb));
		m[make_pair(k, cb)] = re;
		return re;
	}
	else {
		ppp = solve(pp, qq, k / 2, b);
		qqq = solve(pp, qq, (k + 2) / 2, b);
		bb++;
		cb = b;
		b = bb;
		re = solve(ppp, qqq, (k + 2) / 2, b);
		s.insert(make_pair(k, cb));
		m[make_pair(k, cb)] = re;
		return re;
	}
}

