	q.read(n);
	perm a[7];
	a[1]=p;
	a[2]=q;
	for(int i=3;i<=6;i++)
		a[i]=a[i-1]*(~a[i-2]);
	if(m<=6)
	{
		a[m].out();
		return 0;
	}
	perm g=q*(~p)*(~q)*p;
	(Pow(g,(m-1)/6)*a[(m-1)%6+1]*Pow(~g,(m-1)/6)).out();
	return 0;
}

