vector<int> mat_mul(const vector<int>& a, const vector<int>& b, int& n) {
  vector<int> ret(n+1);
  for(int i=1; i<=n; i++) {
    ret[i] = a[b[i]];
  }
  return ret;
}

vector<int> mat_pow(vector<int>& mat, int n, int& size) {
  vector<int> res(size+1);
  vector<int> a = mat;
  for(int i=1; i<=size; i++) {
    res[i] = i;
  }
  while(n > 0) {
    if (n&1) {
      res = mat_mul(res, a, size);
    }
    a = mat_mul(a, a, size); n >>= 1;
  }
  return res;
}

vector<int> mat_inv(const vector<int>& a, const int& n) {
  vector<int> ret(n+1);
  for(int i=1; i<=n; i++) {
    ret[a[i]] = i;
  }
  return ret;
}

