	int nfull=(want-2)/6,nrem=(want-2)%6;
	vector<int> leftperiod=combine(b,combine(ainv,combine(binv,a)));
	vector<vector<int>> mid(6);
	mid[0]=b;
	mid[1]=combine(b,ainv);
	mid[2]=combine(b,combine(ainv,binv));
	mid[3]=combine(b,combine(ainv,combine(binv,combine(a,binv))));
	mid[4]=combine(b,combine(ainv,combine(binv,combine(a,combine(a,binv)))));
	mid[5]=combine(b,combine(ainv,combine(binv,combine(a,combine(b,combine(a,binv))))));
	vector<int> leftfull=pw(leftperiod,nfull);
	ans=combine(leftfull,combine(mid[nrem],inv(leftfull)));
}

void run() {
	scanf("%d%d",&n,&want);
	a=vector<int>(n); REP(i,n) scanf("%d",&a[i]),--a[i];
	b=vector<int>(n); REP(i,n) scanf("%d",&b[i]),--b[i];
	solve();
	REPSZ(i,ans) { if(i!=0) printf(" "); printf("%d",ans[i]+1); } puts("");
}

