namespace PermutationSpace{
	const int PermutationLen=100000;
	struct Permutation{
		int P[PermutationLen+1];
		Permutation(){for (int i=0;i<=PermutationLen;i++) P[i]=i;}
		inline int & operator [] (const int &index){return P[index];}
	};
	Permutation operator * (Permutation A,Permutation B){
		Permutation C;
		for (int i=1;i<=PermutationLen;i++) C[i]=A[B[i]];
		return C;
	}

	Permutation Inv(Permutation C){
		Permutation D;
		for (int i=1;i<=PermutationLen;i++) D[C[i]]=i;
		return D;
	}
	Permutation Pow(Permutation D,int p){
		bool vis[PermutationLen+1];
		int stk[PermutationLen],top;
		Permutation E;
		memset(vis,0,sizeof(vis));
		for (int i=1;i<=PermutationLen;i++){
			if (vis[i]) continue;
			top=0;
			for (int now=i;!vis[now];now=D[now])
				stk[top++]=now,vis[now]=true;
			for (int j=0;j<top;j++) E[stk[j]]=stk[(j+p)%top];
		}
		return E;
	}
}

