using vi = vector<int>;

int const MAXN=110000;

vi pneg(const vi &p) {
	vi P(sz(p));
	rep(i,0,sz(p)) P[p[i]]=i;
	return P;
}

void pmul(const vi &p,const vi &q,vi &r) {
	static int tmp[MAXN];
	rep(i,0,sz(p)) tmp[i]=p[q[i]];
	rep(i,0,sz(p)) r[i]=tmp[i];
}

vi ppow(vi p, int n) {
	vi r(sz(p));
	rep(i,0,sz(p)) r[i]=i;
	while (n>0) {
		if (n%2) pmul(r,p,r);
		pmul(p,p,p);
		n/=2;
	}
	return r;
}

int main() {
	cin.tie(0);
	ios_base::sync_with_stdio(0);
	cout<<fixed<<setprecision(10);

	int N,K;
	cin>>N>>K;
	K--;

	vi p(N),q(N);
	rep(i,0,N) {
		cin>>p[i];
		p[i]--;
	}
	rep(i,0,N) {
		cin>>q[i];
		q[i]--;
	}

	vi P=pneg(p);
	vi Q=pneg(q);

	// a=qPQp
	vi a(N);
	pmul(q,P,a);
	pmul(a,Q,a);
	pmul(a,p,a);

	// a=a^(K/6)
	a=ppow(a,K/6);

	vi b(N);
	switch (K%6) {
		case 0:
			b=p;
			break;
		case 1:
			b=q;
			break;
		case 2:
			// b=qP
			pmul(q,P,b);
			break;
		case 3:
			// b=qPQ
			pmul(q,P,b);
			pmul(b,Q,b);
			break;
		case 4:
			// b=qPQpQ
			pmul(q,P,b);
			pmul(b,Q,b);
			pmul(b,p,b);
			pmul(b,Q,b);
			break;
		case 6:
			// b=qPQppQ
			pmul(q,P,b);
			pmul(b,Q,b);
			pmul(b,p,b);
			pmul(b,p,b);
			pmul(b,Q,b);
			break;
		default:
			assert(false);
	}

	vi res(N);
	pmul(a,b,res);
	pmul(res,pneg(a),res);

	rep(i,0,N) cout<<res[i]+1<<" \n"[i+1==N];
}

