    Permutation P(p), Q(q);
    Permutation O(N);
    int k = (K - 1) % 6;
    if (k == 0) { O *= P; }
    else if (k == 1) { O *= Q; }
    else if (k == 2) { O *= Q / P; }
    else if (k == 3) { O *= Q / P / Q; }
    else if (k == 4) { O *= Q / P / Q * P / Q; }
    else if (k == 5) { O *= Q / P / Q * P * P / Q; }
    Permutation S = (Q / P / Q * P).pow((K - 1) / 6);
    Permutation A = S * O / S;
    rep(i, N) { cout << A[i] + 1 << endl; }
}

