struct perm {
  vector<int> a;
  perm(int n) : a(n) {}
  int size() { return a.size(); }
  int &operator[](int k) { return a[k]; }
};

perm operator*(perm a, perm b) {
  const int n = a.size();
  perm ans(n);
  REP(i, n) ans[i] = a[b[i]];
  return ans;
}

perm inv(perm a) {
  const int n = a.size();
  perm ans(n);
  REP(i, n) ans[a[i]] = i;
  return ans;
}

perm pow(perm a, int b) {
  const int n = a.size();
  perm ans(n);
  REP(i, n) ans[i] = i;
  while (b > 0) {
    if (b & 1) ans = ans * a;
    a = a * a;
    b >>= 1;
  }
  return ans;
}

void print(perm a) {
  for (int i = 0; i < a.size(); i++) {
    if (i > 0) cout << ' ';
    cout << a[i] + 1;
  }
  cout << '\n';
}

int main() {
  int N, K;
  cin >> N >> K;
  perm P(N), Q(N);
  REP(i, N) cin >> P[i], P[i]--;
  REP(i, N) cin >> Q[i], Q[i]--;
  auto X = Q * inv(P) * inv(Q) * P;
  auto Y = inv(P) * Q * P * inv(Q);
  vector<perm> A;
  A.push_back(P);
  A.push_back(Q);
  for (int i = 0; i < 5; i++) {
    A.push_back(A[i + 1] * inv(A[i]));
  }
  A.erase(A.begin());
  if (K == 1) {
    print(P);
    return 0;
  }
  K -= 2;
  perm ans = pow(X, K / 6) * A[K % 6] * pow(Y, K / 6);
  print(ans);
}

#include <bits/stdc++.h>
#define REP(i, n) for (int i = 0; (i) < (int)(n); ++ (i))
#define ALL(x) begin(x), end(x)
using ll = long long;
using namespace std;

vector<int> invert(vector<int> const & p) {
    vector<int> q(p.size());
    REP (i, p.size()) {
        q[p[i]] = i;
    }
    return q;
}

vector<int> multiply(vector<int> const & p, vector<int> const & q) {
    assert (p.size() == q.size());
    vector<int> r(p.size());
    REP (i, p.size()) {
        r[i] = p[q[i]];
    }
    return r;
}

vector<int> power(vector<int> p, ll k) {
    vector<int> q(p.size());
    iota(ALL(q), 0);
    for (ll i = 1; i <= k; i <<= 1) {
        if (k & i) q = multiply(q, p);
        p = multiply(p, p);
    }
    return q;
}

vector<int> solve(int n, ll k, vector<int> const & p, vector<int> const & q) {
    int kq = (k - 1) / 6;
    int kr = (k - 1) % 6;
    vector<int> a = power(multiply(q, multiply(invert(p), multiply(invert(q), p))), kq);
    switch (kr) {
        case 3:
            a = multiply(a, q);
            break;
        case 4:
        case 5:
            a = multiply(a, multiply(q, invert(p)));
            break;
    }
    vector<int> b =
        kr == 0 ? p :
        kr == 1 ? q :
        kr == 2 ? multiply(q, invert(p)) :
        kr == 3 ? invert(p) :
        kr == 4 ? invert(q) :
                  multiply(invert(q), p);
    return multiply(a, multiply(b, invert(a)));
}

int main() {
    // input
    int n; ll k; cin >> n >> k;
    vector<int> p(n);
    REP (i, n) {
        cin >> p[i];
        -- p[i];
    }
    vector<int> q(n);
    REP (i, n) {
        cin >> q[i];
        -- q[i];
    }

    // solve
    vector<int> r = solve(n, k, p, q);

    // output
    REP (i, n) {
        cout << r[i] + 1 << ' ';
    }
    cout << endl;
    return 0;
}

#include <bits/stdc++.h>
using namespace std;

vector<int> inv(vector<int> a) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[a[i]] = i;
  }
  return ret;
}

vector<int> mul(vector<int> a, vector<int> b) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[i] = a[b[i]];
  }
  return ret;
}

vector<int> power(vector<int> a, int b) {
  int n = (int) a.size();
  vector<int> ret(n);
  iota(ret.begin(), ret.end(), 0);
  while (b) {
    if (b & 1) {
      ret = mul(ret, a);
    }
    b >>= 1;
    a = mul(a, a);
  }
  return ret;
}

void output(vector<int> a) {
  for (int i = 0; i < (int) a.size(); i++) {
    if (i) {
      cout << " ";
    }
    cout << a[i] + 1;
  }
  cout << '\n';

  exit(0);
}

int main() {
  int n, k;
  cin >> n >> k;
  vector<int> a(n), b(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i];
    a[i]--;
  }
  for (int i = 0; i < n; i++) {
    cin >> b[i];
    b[i]--;
  }
  if (k == 1) {
    output(a);
  }
  vector<int> A = inv(a);
  vector<int> B = inv(b);
  vector<int> bABa = mul(b, mul(A, mul(B, a)));
  vector<int> AbaB = mul(A, mul(b, mul(a, B)));

  vector<int> l[6];
  l[0] = b;
  l[1] = mul(b, A);
  l[2] = mul(b, mul(A, B));
  l[3] = mul(bABa, B);
  l[4] = mul(bABa, mul(a, B));
  l[5] = mul(bABa, mul(b, mul(a, B)));

  vector<int> f = power(AbaB, (k - 2) / 6);
  f = mul(l[(k - 2) % 6], f);
  f = mul(power(bABa, (k - 2) / 6), f);
  output(f);
  return 0;
}

