// q(p(i))
vector<i64> composite(const vector<i64> &p, const vector<i64> &q){
    const i64 n=p.size();
    vector<i64> res(n);
    rep(i,0,n) res[i]=q[p[i]];
    return res;
}

vector<i64> inverse(const vector<i64> &p){
    const i64 n=p.size();
    vector<i64> res(n);
    rep(i,0,n) res[p[i]]=i;
    return res;
}

vector<i64> pow(vector<i64> p,i64 k){
    const i64 n=p.size();
    vector<i64> res(n);
    rep(i,0,n){
        res[i]=i;
    }
    while(k){
        if(k&1){
            res=composite(res,p);
        }
        p=composite(p,p);
        k>>=1;
    }
    return res;
}

vector<i64> f(const vector<i64> &p,const vector<i64> &q){
    const i64 n=p.size();
    vector<i64> res(n);
    rep(i,0,n) res[p[i]]=q[i];
    return res;
}

