vector<int> inv(const vector<int>& a) {
    int n = a.size();
    vector<int> res(n);
    for (int i = 0; i < n; ++i)
        res[a[i]] = i;
    return res;
}


vector<int> mult(const vector<int>& a, const vector<int>& b) {
    int n = a.size();
    vector<int> res(n);
    for (int i = 0; i < n; ++i) {
        res[i] = a[b[i]];
    }
    return res;
}

vector<int> power(vector<int> a, int n) {
    int sz = a.size();
    vector<int> res(sz);
    for (int i = 0; i < sz; ++i)
        res[i] = i;
    while (n) {
        if (n & 1)
            res = mult(res, a);
        a = mult(a, a);
        n /= 2;
    }
    return res;
}



void solve() {
    int n, k;
    cin >> n >> k;
    vector<int> p(n), q(n);
    for (int i = 0; i < n; ++i) {
        cin >> p[i];
        --p[i];
    }
    for (int i = 0; i < n; ++i) {
        cin >> q[i];
        --q[i];
    }
    int tr = 20;
    if (k < tr) {
        vector<vector<int> > res(k);
        res[0] = p;
        res[1] = q;
        for (int i = 2; i < k; ++i)
            res[i] = mult(res[i - 1], inv(res[i - 2]));
        for (int i = 0; i < n; ++i) {
            cout << res[k - 1][i] + 1 << " ";
        }
        cout << endl;
        return;
    }
    int x = (k + 1) / 6;
    int r = (k + 1) % 6;
    vector<int> p1 = mult(mult(q, inv(p)), mult(inv(q), p));
    vector<int> p2 = mult(mult(inv(p), q), mult(p, inv(q)));
    p1 = power(p1, x);
    int y = x - 1;
    if (r >= 3)
        ++y;
    p2 = power(p2, y);
    vector<int> p3;
    if (r == 0) {
        p3 = inv(q);
    }
    if (r == 1) {
        p3 = mult(p, inv(q));
    }
    if (r == 2) {
        p3 = mult(q, mult(p, inv(q)));
    }
    if (r == 3) {
        p3 = q;
    }
    if (r == 4) {
        p3 = mult(q, inv(p));
    }
    if (r == 5) {
        p3 = mult(mult(q, inv(p)), inv(q));
    }
    vector<int> res = mult(p1, mult(p3, p2));
    for (int i = 0; i < res.size(); ++i)
        cout << res[i] + 1 << " ";
    cout << endl;







}

