const int maxn = 1e5;
int n, k;

struct perm {
	int n, a[maxn + 3];

	perm(int m) {
		n = m;
		for (int i = 1; i <= n; i++) {
			a[i] = i;
		}
	}

	friend perm operator * (perm p, perm q) {
		perm r(p.n);
		for (int i = 1; i <= p.n; i++) {
			r.a[i] = p.a[q.a[i]];
		}
		return r;
	}

	perm inv() {
		perm r(n);
		for (int i = 1; i <= n; i++) {
			r.a[a[i]] = i;
		}
		return r;
	}

	void print() {
		for (int i = 1; i <= n; i++) {
			printf("%d%c", a[i], " \n"[i == n]);
		}
	}
};

perm g(perm p, perm q) {
	return p * q * p.inv();
}

perm qpow(perm p, int k) {
	perm q(n);
	for (; k; k >>= 1, p = p * p) {
		if (k & 1) q = p * q;
	}
	return q;
}

int main() {
	scanf("%d %d", &n, &k);
	perm p(n), q(n), r(n);
	for (int i = 1; i <= n; i++) {
		scanf("%d", &p.a[i]);
	}
	for (int i = 1; i <= n; i++) {
		scanf("%d", &q.a[i]);
	}
	int x = (k - 1) / 6;
	r = q * p.inv() * q.inv() * p;
	r = qpow(r, x);
	k -= x * 6;
	if (k == 1) {
		g(r, p).print();
	} else if (k == 2) {
		g(r, q).print();
	} else if (k == 3) {
		g(r, q * p.inv()).print();
	} else if (k == 4) {
		g(r, q * p.inv() * q.inv()).print();
	} else if (k == 5) {
		g(r, q * p.inv() * q.inv() * p * q.inv()).print();
	} else {
		g(r, q * p.inv() * q.inv() * p * p * q.inv()).print();
	}
	return 0;
}

#include <bits/stdc++.h>
#define sz(c)      int(c.size())
#define rep(i,a,b) for (int i=a; i<(b); ++i)
#define per(i,a,b) for (int i=(b)-1; i>=(a); --i)
using namespace std;
using ll = long long;
using vi = vector<int>;

vi pinv(const vi &p) {
	vi res(sz(p));
	rep(i,0,sz(p)) res[p[i]]=i;
	return res;
}

vi pcompose(const vi &p,const vi &q) {
	vi res(sz(p));
	rep(i,0,sz(p)) res[i]=p[q[i]];
	return res;
}

vi ppow(vi p,int n) {
	vi res(sz(p));
	rep(i,0,sz(p)) res[i]=i;
	while (n>0) {
		if (n%2) res=pcompose(res,p);
		p=pcompose(p,p);
		n/=2;
	}
	return res;
}

int main() {
	cin.tie(0);
	ios_base::sync_with_stdio(0);
	cout<<fixed<<setprecision(10);

	int N,K;
	cin>>N>>K;
	K--;

	vi p(N),q(N);
	rep(i,0,N) {
		cin>>p[i];
		p[i]--;
	}
	rep(i,0,N) {
		cin>>q[i];
		q[i]--;
	}

	vi P=pinv(p);
	vi Q=pinv(q);

	vi a=ppow(pcompose(q,pcompose(P,pcompose(Q,p))),K/6);
	vi b;
	switch (K%6) {
		case 0:
			b=p;
			break;
		case 1:
			b=q;
			break;
		case 2:
			b=pcompose(q,P);
			break;
		case 3:
			b=pcompose(q,pcompose(P,Q));
			break;
		case 4:
			b=pcompose(q,pcompose(P,pcompose(Q,pcompose(p,Q))));
			break;
		case 5:
			b=pcompose(q,pcompose(P,pcompose(Q,pcompose(p,pcompose(p,Q)))));
			break;
		default:
			assert(false);
	}

	vi res=pcompose(a,pcompose(b,pinv(a)));
	rep(i,0,N) cout<<res[i]+1<<" \n"[i+1==N];
}

