#define REP(NAME, NUM) for (int NAME = 0; NAME < (NUM); ++NAME)
#define BREP(NAME, NUM) for (int NAME = (NUM)-1; NAME >= 0; --NAME)
#define ALL(NAME) (NAME).begin(), (NAME).end()
#define VEC(TYPE, A, NAME, INIT) vector<TYPE> NAME(A, INIT)
#define VEC2(TYPE, A, B, NAME, INIT) vector<vector<TYPE>> NAME(A, vector<TYPE>(B, (INIT)))

template< class T >
const vector<T> operator*( const vector<T>& a, const vector<T>& b )
{
	T size = a.size();
	vector<T> ret( size );
	REP( i , size ) ret[i] = a[b[i]];
	return ret;
}

template< class T >
const vector<T> operator~( const vector<T>& a )
{
	T size = a.size();
	vector<T> ret( size );
	REP( i , size ) ret[a[i]] = i;
	return ret;
}

template< class T >
void makeUnitPermutation( vector<T>& a )
{
	T size = a.size();
	REP( i , size ) a[i] = i;
}

void print( const vector<ll>& v )
{
	for( ll i = 0, end = v.size() - 1; i < end; ++i )
		cout << v[i] + 1 << " ";
	cout << v[v.size()-1] + 1 << endl;
}

int main()
{
	ll n, k;
	cin >> n >> k;
	vector<ll> p( n );
	vector<ll> q( n );
	REP(i,n) {cin>>p[i]; --p[i];}
	REP(i,n) {cin>>q[i]; --q[i];}

	auto pInv = ~p;
	auto qInv = ~q;
	auto cycle = q * pInv * qInv * p;

	auto kk = ( k - 1 ) / 6;
	auto cycleK = cycle; makeUnitPermutation( cycleK );
	{
		auto x = cycle;
		while (kk > 0)
		{
			if (kk & 1) cycleK = cycleK * x;
			x = x * x;
			kk >>= 1;
		}
	}

	auto center = cycleK; makeUnitPermutation( center );
	REP(i,( k - 1 ) % 6 + 1)
	{
		if( i == 0 ) center = p;
		else if( i == 1 ) center = q;
		else if( i == 2 ) center = center * pInv;
		else if( i == 3 ) center = cycle * pInv;
		else if( i == 4 ) center = cycle * qInv;
		else if( i == 5 ) center = cycle * p * qInv;
	}

	auto ans =cycleK * center * ( ~cycleK );

	print( ans );
	return 0;
}

