vector<int> inv(const vector<int> &x) {
	vector<int> a(x.size());
	for(int i = 0; i < n; ++i) a[x[i]] = i;
	return a;
}
vector<int> comp(const vector<int> &x, const vector<int> &y) {
	vector<int> a(x.size());
	for(int i = 0; i < n; ++i) a[i] = y[x[i]];
	return a;
}
void print(const vector<int> &a) {
	for(int i = 0; i < n; ++i) cout << (a[i] + 1) << ' '; cout << endl;
}

vector<int> qpow(vector<int> a, int k) {
	vector<int> r(a.size());
	for(int i = 0; i < n; ++i) r[i] = i;
	while(k) {
		if(k & 1) r = comp(r, a);
		a = comp(a, a); k >>= 1;
	}
	return r;
}
vector<int> pre() {
	int cnt = (k - 1) / 3 * 2 - 1;
	eprintf("k = %d, pre = %d\n", k, cnt);
	auto r = qpow(comp(comp(comp(inv(q), p),q),inv(p)), cnt / 4);
	cnt -= cnt / 4 * 4;
	if(cnt) cnt--, r = comp(r, inv(q));
	if(cnt) cnt--, r = comp(r, p);
	if(cnt) cnt--, r = comp(r, q);
	return r;
}

vector<int> post() {
	vector< vector<int> > off;
	if((k + 1) / 3 % 2 == 1)
		off = {q, p, inv(q), inv(p)};
	else
		off = {inv(q), inv(p), q, p};
	int cnt = (k + 1) / 3 * 2 - 1;
	eprintf("k = %d, post = %d\n", k, cnt);
	auto r = qpow(comp(comp(comp(off[0], off[1]), off[2]), off[3]), cnt / 4);
	cnt -= cnt / 4 * 4;
	if(cnt) cnt--, r = comp(r, off[0]);
	if(cnt) cnt--, r = comp(r, off[1]);
	if(cnt) cnt--, r = comp(r, off[2]);
	return r;
}

void bf() {
	if(k == 1) print(p);
	else if(k == 2) print(q);
	else {
		k -= 2;
		while(k--) {
			auto x = comp(inv(p), q);
			p = q; q = x;
		}
		print(q);
	}
}

