#include <vector>
using namespace std;

vector<int> apply(const vector<int> &a, const vector<int> &b)
{
	vector<int> r(a.size());

	for (int i = 0; i < b.size(); i++) r[i] = a[b[i]];

	return move(r);
}

vector<int> pow(const vector<int> &a, int k)
{
	vector<int> r(a.size());

	if (k == 0){
		for (int i = 0; i < a.size(); i++) r[i] = i;
	}
	else{
		auto h = pow(a, k / 2);
		r = apply(h, h);
		if (k % 2){
			for (int i = 0; i < r.size(); i++) r[i] = a[r[i]];
		}
	}

	return move(r);
}


int main()
{
	int N, K; scanf ("%d %d", &N, &K);
	vector<int> p(N), q(N), P(N), Q(N);
	for (int i = 0; i < N; i++){
		scanf ("%d", &p[i]); p[i]--;
		P[p[i]] = i;
	}
	for (int i = 0; i < N; i++){
		scanf ("%d", &q[i]); q[i]--;
		Q[q[i]] = i;
	}

	vector<int> r;
	if (K == 1) r = p;
	else{
		int u = (K - 2) / 6;
		vector<int> X = pow(apply(P, apply(q, apply(p, Q))), u);

		vector<int> C;

		if (K % 6 == 0) C = apply(p, Q);
		if (K % 6 == 1) C = apply(q, apply(p, Q));
		if (K % 6 == 2) C = q;
		if (K % 6 == 3) C = apply(q, P);
		if (K % 6 == 4) C = apply(q, apply(P, Q));
		if (K % 6 == 5) C = Q;

		int v = (K + 1) / 6;
		vector<int> Y = pow(apply(q, apply(P, apply(Q, p))), v);

		r = apply(Y, apply(C, X));
	}

	for (int i = 0; i < N; i++) printf ("%d%c", r[i] + 1, i + 1 < N ? ' ' : '\n');

	return 0;
}

