vector<int> id (int n) {
  vector<int> ans;
  for (int i = 0; i < n; i++) {
    ans.push_back(i);
  }
  return ans;
}

vector<int> exp (vector<int> p, int k) {
  if (k == 0) return id(p.size());
  if (k % 2 == 0) {
    auto half = exp(p, k / 2);
    return half * half;
  }
  return p * exp(p, k - 1);
}

