using namespace std;

using perm = vector<int>;
perm operator*(perm a, perm b){
    perm *r = new perm();
    r->resize(a.size());
    for(size_t i=0;i<a.size();i++) (*r)[i] = a[b[i]-1];
    return *r;
}
perm inv(perm a){
    perm *r = new perm();
    r->resize(a.size());
    for(size_t i=0;i<a.size();i++) (*r)[a[i]-1] = i+1;
    return *r;
}
perm pow(perm a, int n){
    perm *r = new perm();
    r->resize(a.size());
    for(size_t i=0;i<a.size();i++) (*r)[i] = i+1;
    if(n==0) return *r;
    if(n==1) return *r*a;
    delete r;
    if(n%2) return pow(a*a, n/2)*a;
    return pow(a*a, n/2);
}

