//!============================================!//
//!    8888ba.88ba             oo              !//
//!    88  '8b  '8b                            !//
//!    88   88   88  .d8888b.  dP  88d888b.    !//
//!    88   88   88  88'  '88  88  88'  '88    !//
//!    88   88   88  88.  .88  88  88    88    !//
//!    dP   dP   dP  '88888P8  dP  dP    dP    !//
//!============================================!//
int main()
{
    int N, K;
    std::cin >> N >> K, K--;
    std::vector<int> p(N), q(N), rp(N), rq(N);
    for (int i = 0; i < N; i++) { std::cin >> p[i], p[i]--, rp[p[i]] = i; }
    for (int i = 0; i < N; i++) { std::cin >> q[i], q[i]--, rq[q[i]] = i; }
    auto hoge = [&](const std::vector<int>& p, const std::vector<int>& q) -> std::vector<int> {
        std::vector<int> ans(N);
        for (int i = 0; i < N; i++) { ans[i] = p[q[i]]; }
        return ans;
    };
    auto id = [&]() {
        std::vector<int> ans(N);
        std::iota(ans.begin(), ans.end(), 0);
        return ans;
    };
    const std::vector<int> P = hoge(hoge(q, rp), hoge(rq, p));
    const auto C = K % 6 == 0 ? p : K % 6 == 1 ? q : K % 6 == 2 ? hoge(q, rp) : K % 6 == 3 ? rp : K % 6 == 4 ? rq : hoge(rq, p);
    const auto R = K % 6 <= 2 ? id() : K % 6 == 3 ? q : hoge(q, rp);
    auto power = [&](auto&& self, const std::vector<int>& p, const int K) -> std::vector<int> {
        if (K == 0) {
            return id();
        } else if (K % 2 == 1) {
            return hoge(self(self, p, K - 1), p);
        } else {
            const auto pp = self(self, p, K / 2);
            return hoge(pp, pp);
        }
    };
    const auto Q = hoge(power(power, P, K / 6), R);
    std::vector<int> RQ(N);
    for (int i = 0; i < N; i++) { RQ[Q[i]] = i; }
    const auto ans = hoge(Q, hoge(C, RQ));
    for (int i = 0; i < N; i++) { std::cout << ans[i] + 1 << " "; }
    std::cout << std::endl;
    return 0;
}

