using namespace std;

vector <int> inv(const vector <int> &v) {
    int i;
    vector <int> iv(v.size());

    for (i = 0; i < v.size(); i++) iv[v[i]] = i;

    return iv;
}

vector <int> mul(const vector <int> &v, const vector <int> &w) {
    int i;
    vector <int> z;

    for (i = 0; i < v.size(); i++) z.push_back(v[w[i]]);

    return z;
}

vector <int> pow(const vector <int> &v, int n) {
    int i;
    vector <int> w = v, z;

    for (i = 0; i < v.size(); i++) z.push_back(i);

    while (n > 0) {
        if (n & 1) z = mul(z, w);
        w = mul(w, w);
        n >>= 1;
    }

    return z;
}

int main() {
    int n, k, i;
    vector <int> p, q, ip, iq, v, a, b, ans;

    scanf("%d %d", &n, &k);

    for (i = 0; i < n; i++) {
        int x;
        scanf("%d", &x);
        p.push_back(x - 1);
    }

    for (i = 0; i < n; i++) {
        int x;
        scanf("%d", &x);
        q.push_back(x - 1);
    }

    ip = inv(p);
    iq = inv(q);

    v = pow(mul(q, mul(ip, mul(iq, p))), (k - 1) / 6);

    if (k % 6 == 1) {
        a = v;
        b = p;
    } else if (k % 6 == 2) {
        a = v;
        b = q;
    } else if (k % 6 == 3) {
        a = v;
        b = mul(q, ip);
    } else if (k % 6 == 4) {
        a = mul(v, q);
        b = ip;
    } else if (k % 6 == 5) {
        a = mul(v, mul(q, ip));
        b = iq;
    } else {
        a = mul(v, mul(q, ip));
        b = mul(iq, p);
    }

    ans = mul(a, mul(b, inv(a)));

    for (i = 0; i < v.size(); i++) {
        if (i > 0) putchar(' ');
        printf("%d", ans[i] + 1);
    }
    puts("");

    return 0;
}

